import styled from "styled-components";

export const CartitemContainer = styled.div`
	width: 100%;
	display: flex;
	height: 80px;
	margin-bottom: 15px;
`;

export const CartitemImage = styled.img`
	width: 30%;
`;

export const ItemDetailsContainer = styled.div`
	width: 70%;
	display: flex;
	flex-direction: column;
